function isArray(obj) {
  return toString.call(obj) === "[object Array]";
} 

(function(){ return isArray(arguments); })();
// false

isArray([1,2,3])
// true

function isObject(obj) {
  return obj === Object(obj);
}

isObject({});
// true

isObject(1);
// false

function isString(obj) {
  return !!(obj === "" || (obj && obj.charCodeAt && obj.substr));
}

isString("name");
// true

isString(1);
// false

function isNumber(obj) {
  return !!(obj === 0 || (obj && obj.toExponential && obj.toFixed));
}

isNumber(8.4 * 5);
// true

isNumber('1');
// false

function isNan(obj) {
  return obj !== obj;
}

isNan(NaN);
// true

isNaN(undefined);
// true

isNan(undefined);
// false

function isBoolean(obj) {
  return obj === true || obj === false;
}

isBoolean(null);
// false

function isNull(obj) {
  return obj === null;
}

isNull(null);
// true

isNull(undefined);
// false

function isUndefined(obj) {
  return obj === void 0;
}

isUndefined(window.missingVariable);
// true

isUndefined(window);
// false

function isDate(obj) {
  return !!(obj && obj.getTimezoneOffset && obj.setUTCFullYear);
}

isDate(new Date());
// true

function isRegExp(obj) {
  return !!(
    obj &&
    obj.test &&
    obj.exec &&
    (obj.ignoreCase || obj.ignoreCase === false)
  );
}

isRegExp(/test/);
// true

function isUniform(arr) {
  var first = arr[0];
  for (var i = 1; i < arr.length; i++) {
    if (arr[i] !== first) {
      return false;
    }
  }
  return true;
}

isUniform(['1','1','1'])
// true

isUniform([1,1,1])
// true

isUniform(['1','2','3'])
// false

isUniform([1,2,3])
// false

function isEven(num) {
  return num % 2 === 0;
}

isEven(2);
// true

isEven(3);
// false

function isMatch(object, attrs) {
  var keys = Object.keys(attrs),
    length = keys.length;
  if (object == null) return !length;
  var obj = Object(object);
  for (var i = 0; i < length; i++) {
    var key = keys[i];
    if (attrs[key] !== obj[key] || !(key in obj)) return false;
  }
  return true;
}

var obj = {name: 'test', number: 10};

isMatch(obj, {number: 10});
// true

function isAlphaNumeric(str) {
  var code = str.charCodeAt(0);
  if (
    !(code > 47 && code < 58) && // numeric (0-9)
    !(code > 64 && code < 91) && // uppercase (A-Z)
    !(code > 96 && code < 123)
  ) {
    // lowercase (a-z)
    return false;
  }
  return true;
}

isAlphaNumeric('1');
// true

isAlphaNumeric('a');
// true

isAlphaNumeric('?');
// false

function isElement(obj) {
  return !!(obj && obj.nodeType === 1);
}

isElement(document.body);
// true

function isPrime(value) {
  for (var i = 2; i < value; i++) {
    if (value % i === 0) {
      return false;
    }
  }
  return value > 1;
}

isPrime(5);
// true

isPrime(4);
// false

function isArguments(obj){
  return toString.call(obj) === "[object Arguments]";
}

(function(){ return isArguments(arguments); })(1, 2, 3);
// true

isArguments([1,2,3]);
// false

function isFunction(obj){
  return toString.call(obj) === "[object Function]";
}

isFunction(alert);
// true

function isError(obj){
  return toString.call(obj) === "[object Error]";
}

try {
  throw new TypeError("Example");
} catch (o_O) {
  isError(o_O);
}
// true

function isSymbol(obj){
  return toString.call(obj) === "[object Symbol]";
}

isSymbol(Symbol());
// true

function isMap(obj){
