function isWeakSet(obj){
  return toString.call(obj) === "[object WeakSet]";
}

isWeakSet(new WeakSet());
// true

function charCount(str){
  var result = {};
  for(var char of str){
    if(isAlphaNumeric(char)){
      char = char.toLowerCase();
      result[char] = ++result[char] || 1;
    }
  }
  return result
}

